mapper.select:
SELECT
-- 级联表的字段 ,weixiuyuan.weixiuyuan_name as weixiuyuanName ,weixiuyuan.weixiuyuan_nianxian as weixiuyuanNianxian ,weixiuyuan.weixiuyuan_phone as weixiuyuanPhone ,weixiuyuan.weixiuyuan_photo as weixiuyuanPhoto ,weixiuyuan.weixiuyuan_content as weixiuyuanContent ,weixiuyuan.weixiuyuan_delete as weixiuyuanDelete ,xuesheng.xuesheng_name as xueshengName ,xuesheng.xuesheng_id_number as xueshengIdNumber ,xuesheng.xuesheng_phone as xueshengPhone ,xuesheng.xuesheng_photo as xueshengPhoto ,xuesheng.xuesheng_delete as xueshengDelete
FROM guzhangshangbao a left JOIN weixiuyuan weixiuyuan ON a.weixiuyuan_id = weixiuyuan.id left JOIN xuesheng xuesheng ON a.xuesheng_id = xuesheng.id
and a.id in
#{item}
and ( a.xuesheng_id = #{params.xueshengId} )
and a.guzhangshangbao_uuid_number like CONCAT('%',#{params.guzhangshangbaoUuidNumber},'%')
and a.quyu_types = #{params.quyuTypes}
and a.guzhangshangbao_address like CONCAT('%',#{params.guzhangshangbaoAddress},'%')
and a.guzhangshangbao_guzhang like CONCAT('%',#{params.guzhangshangbaoGuzhang},'%')
and a.guzhangshangbao_types = #{params.guzhangshangbaoTypes}
and ( a.weixiuyuan_id = #{params.weixiuyuanId} or a.weixiuyuan_id is null )
and a.chulijieguo_types = #{params.chulijieguoTypes}
and a.weixiuyuan_id IS NOT NULL
and weixiuyuan.weixiuyuan_name like CONCAT('%',#{params.weixiuyuanName},'%')
and weixiuyuan.weixiuyuan_nianxian like CONCAT('%',#{params.weixiuyuanNianxian},'%')
and weixiuyuan.weixiuyuan_phone like CONCAT('%',#{params.weixiuyuanPhone},'%')
and weixiuyuan.weixiuyuan_content like CONCAT('%',#{params.weixiuyuanContent},'%')
and weixiuyuan.weixiuyuan_delete >= #{params.weixiuyuanDeleteStart}
and weixiuyuan.weixiuyuan_delete <= #{params.weixiuyuanDeleteEnd}
and weixiuyuan.weixiuyuan_delete = #{params.weixiuyuanDelete}
and a.xuesheng_id IS NOT NULL
and xuesheng.xuesheng_name like CONCAT('%',#{params.xueshengName},'%')
and xuesheng.xuesheng_id_number like CONCAT('%',#{params.xueshengIdNumber},'%')
and xuesheng.xuesheng_phone like CONCAT('%',#{params.xueshengPhone},'%')
and xuesheng.xuesheng_delete >= #{params.xueshengDeleteStart}
and xuesheng.xuesheng_delete <= #{params.xueshengDeleteEnd}
and xuesheng.xuesheng_delete = #{params.xueshengDelete}
order by a.${params.orderBy} desc
